CREATE TABLE CATEGORY (HJID BIGINT NOT NULL, DOMAIN_ VARCHAR(255), VALUE_ VARCHAR(255), PRIMARY KEY (HJID));
CREATE TABLE CLOUD (HJID BIGINT NOT NULL, DOMAIN_ VARCHAR(255), PATH_ VARCHAR(255), PORT BIGINT, PROTOCOL VARCHAR(255), REGISTERPROCEDURE VARCHAR(255), PRIMARY KEY (HJID));
CREATE TABLE ENCLOSURE (HJID BIGINT NOT NULL, LENGTH_ BIGINT, TYPE_ VARCHAR(255), URL VARCHAR(255) NOT NULL, VALUE_ VARCHAR(255), PRIMARY KEY (HJID), CONSTRAINT U_NCLOSUR_URL UNIQUE (URL));
CREATE TABLE IMAGE (HJID BIGINT NOT NULL, DESCRIPTION VARCHAR(255), HEIGHT INTEGER, LINK VARCHAR(255), TITLE VARCHAR(255), URL VARCHAR(255), WIDTH INTEGER, PRIMARY KEY (HJID));
CREATE TABLE OPENJPA_SEQUENCE_TABLE (ID SMALLINT NOT NULL, SEQUENCE_VALUE BIGINT, PRIMARY KEY (ID));
CREATE TABLE RSS (HJID BIGINT NOT NULL, VERSION_ NUMERIC, CHANNEL_RSS_HJID BIGINT, PRIMARY KEY (HJID));
CREATE TABLE RSSCHANNEL (HJID BIGINT NOT NULL, COPYRIGHT VARCHAR(255), DESCRIPTION VARCHAR(255), DOCS VARCHAR(255), GENERATOR VARCHAR(255), LANGUAGE_ VARCHAR(17), LASTBUILDDATE VARCHAR(255), LINK VARCHAR(255), MANAGINGEDITOR VARCHAR(255), PUBDATE VARCHAR(255), RATING VARCHAR(255), TITLE VARCHAR(255) NOT NULL, TTL BIGINT, WEBMASTER VARCHAR(255), CATEGORY_RSSCHANNEL_HJID BIGINT, CLOUD_RSSCHANNEL_HJID BIGINT, IMAGE_RSSCHANNEL_HJID BIGINT, TEXTINPUT_RSSCHANNEL_HJID BIGINT, PRIMARY KEY (HJID), CONSTRAINT U_RSSCNNL_TITLE UNIQUE (TITLE));
CREATE TABLE RSSCHANNEL_ITEM_RSSITEM (PARENT_RSSCHANNEL_HJID BIGINT, CHILD_RSSITEM_HJID BIGINT);
CREATE TABLE RSSITEM (HJID BIGINT NOT NULL, AUTHOR VARCHAR(255), COMMENTS VARCHAR(255), DESCRIPTION VARCHAR(255), LINK VARCHAR(255), PUBDATE VARCHAR(255), TITLE VARCHAR(255), CATEGORY_RSSITEM_HJID BIGINT, ENCLOSURE_RSSITEM_HJID BIGINT, GUID_ISPERMALINK BOOL, GUID_VALUE VARCHAR(255) NOT NULL, SOURCE__RSSITEM_HJID BIGINT, PRIMARY KEY (HJID), CONSTRAINT U_RSSITEM_GUID_VALUE UNIQUE (GUID_VALUE));
CREATE TABLE SOURCE_ (HJID BIGINT NOT NULL, URL VARCHAR(255), VALUE_ VARCHAR(255), PRIMARY KEY (HJID));
CREATE TABLE TEXTINPUT (HJID BIGINT NOT NULL, DESCRIPTION VARCHAR(255), LINK VARCHAR(255), NAME_ VARCHAR(255), TITLE VARCHAR(255), PRIMARY KEY (HJID));
